import './form.scss';
import React, { useState } from 'react';

function Form(props) {
  const [method, setMethod] = useState('GET');
  const [url, setUrl] = useState('');
  const [body, setBody] = useState('');

  const handleSubmit = e => {
    e.preventDefault();
    const formData = {
      method,
      url,
      body: method === 'POST' || method === 'PUT' ? body : null,
    };
    props.handleApiCall(formData); // send to App
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>
          <span>URL: </span>
          <input
            name="url"
            type="text"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />
          <button type="submit">GO!</button>
        </label>

        <label className="methods">
          <span
            id="get"
            className={method === 'GET' ? 'active' : ''}
            onClick={() => setMethod('GET')}
          >
            GET
          </span>
          <span
            id="post"
            className={method === 'POST' ? 'active' : ''}
            onClick={() => setMethod('POST')}
          >
            POST
          </span>
          <span
            id="put"
            className={method === 'PUT' ? 'active' : ''}
            onClick={() => setMethod('PUT')}
          >
            PUT
          </span>
          <span
            id="delete"
            className={method === 'DELETE' ? 'active' : ''}
            onClick={() => setMethod('DELETE')}
          >
            DELETE
          </span>
        </label>

        {/* Show textarea only for POST/PUT */}
        {(method === 'POST' || method === 'PUT') && (
          <label>
            <span>Body:</span>
            <textarea
              value={body}
              onChange={(e) => setBody(e.target.value)}
            />
          </label>
        )}
      </form>
    </>
  );
}

export default Form;
