// src/App.js

import React, { useState } from "react";
import Header from "./components/Header/Header";   // Top header
import Form from "./components/Form/";       // Form for API input
import Results from "./components/Results/Results"; // Shows API response
import History from "./components/History"; // ✅ NEW: Request history
import Footer from "./components/Footer/Footer";   // Bottom footer

function App() {
  const [data, setData] = useState(null);       // Stores API response
  const [loading, setLoading] = useState(false); // Tracks loading state
  const [history, setHistory] = useState([]);    // Stores request history

  // Handles API calls
  const handleApiCall = async (requestParams) => {
    setLoading(true);

    try {
      const response = await fetch(requestParams.url, {
        method: requestParams.method || "GET",
        headers: requestParams.headers || {},
        body: requestParams.body ? JSON.stringify(requestParams.body) : null,
      });

      const result = await response.json();
      setData(result);

      // Save request to history
      setHistory((prev) => [
        ...prev,
        { url: requestParams.url, method: requestParams.method },
      ]);
    } catch (err) {
      console.error("API call failed:", err);
    } finally {
      setLoading(false);
    }
  };

  // ✅ Render components
  return (
    <React.Fragment>
      <Header />

      {/* Form for making API requests */}
      <Form handleApiCall={handleApiCall} />

      {/* Separate History & Results */}
      <History history={history} />
      <Results data={data} loading={loading} />

      <Footer />
    </React.Fragment>
  );
}

export default App;
