import React, { useReducer, useEffect } from 'react';
import axios from 'axios';
import './app.scss';

import Header from './components/header';
import Footer from './components/footer';
import Form from './components/form';
import Results from './components/results';
import History from './components/history'; // ðŸ‘ˆ keeps track of past calls

// --- 1. Initial state for reducer ---
const initialState = {
  data: null,       // holds the API response
  request: {},      // holds the current request (url, method, body)
  loading: false,   // true while API request is running
  history: [],      // saves past requests + responses
};

// --- 2. Reducer function to handle state updates ---
const reducer = (state, action) => {
  const { type, payload } = action;

  switch (type) {
    case 'START_REQUEST': 
      // when Form submits a request, save it + set loading true
      return {
        ...state,
        request: payload,
        loading: true,
      };

    case 'FINISH_REQUEST': 
      // when API returns, stop loading, save data + push into history
      return {
        ...state,
        loading: false,
        data: payload,
        history: [...state.history, { request: state.request, data: payload }],
      };

    case 'SHOW_HISTORY': 
      // when user clicks a past request, load its data into state
      return {
        ...state,
        loading: 'history',
        data: payload.data,
        request: payload.request,
      };

    default:
      return state;
  }
};

function App() {
  // --- 3. useReducer replaces multiple useState hooks ---
  const [state, dispatch] = useReducer(reducer, initialState);
  const { request, data, loading, history } = state; // easier access

  // --- 4. Triggered when Form submits ---
  const callApi = (requestParams) => {
    // payload includes: { method, url, body }
    dispatch({ type: 'START_REQUEST', payload: requestParams });
  };

  // --- 5. Triggered when user clicks a history entry ---
  const showHistoryData = (entry) => {
    dispatch({ type: 'SHOW_HISTORY', payload: entry });
  };

  // --- 6. useEffect runs API calls whenever request changes ---
  useEffect(() => {
    const fetchData = async () => {
      // only run if loading is true and request has method + url
      if (loading === true && request.method && request.url) {
        try {
          const response = await axios({
            method: request.method,
            url: request.url,
            data: request.body || {}, // ðŸ‘ˆ include body if present
          });

          // save successful response
          dispatch({ type: 'FINISH_REQUEST', payload: response.data });

        } catch (error) {
          // save error as data (so Results can display it)
          console.error("API call failed:", error);
          dispatch({ type: 'FINISH_REQUEST', payload: { error: error.message } });
        }
      }
    };

    fetchData();
  }, [request, loading]); 
  // ðŸ‘† only re-run when request or loading changes

  // --- 7. What gets rendered on the page ---
  return (
    <React.Fragment>
      <Header />

      {/* show current request */}
      <div>Request Method: {request.method}</div>
      <div>URL: {request.url}</div>

      {/* Pass callback into Form so it can call API */}
      <Form handleApiCall={callApi} />

      {/* Results component shows the API data prettified */}
      <Results data={data} loading={loading} history={history} />

      History shows clickable past requests
      <History history={history} showHistory={showHistoryData} />

      <Footer />
    </React.Fragment>
  );
}

export default App;
